<?php// script may run longer than the typical timeout of 30-90 secondsset_time_limit(600);// Database settings -- change accordinglydefine("DB_LOGIN", "");define("DB_PASSWORD", "");define("DB_NAME", "");// Path to csv folder (replace 'csv' with your own name if desired)// Sample remote server path: /home/username/csv/// Typical WAMP server path: c:/wamp/www/csv/define ("CSV_FILE_PATH", "");// Open a connection to the localhost databas@$db = mysqli_connect('localhost', DB_LOGIN, DB_PASSWORD, DB_NAME);if (mysqli_connect_errno()) {    printf("Connect failed: %s\n", mysqli_connect_error());    exit();}//define table names$table[1] = "block";$table[2] = "chart";$table[3] = "conv";$table[4] = "play";$table[5] = "defense";$table[6] = "drive";$table[7] = "fgxp";$table[8] = "fumble";$table[9] = "game";$table[10] = "intercpt";$table[11] = "kicker";$table[12] = "koff";$table[13] = "offense";$table[14] = "oline";$table[15] = "pass";$table[16] = "penalty";$table[17] = "player";$table[18] = "punt";$table[19] = "redzone";$table[20] = "rush";$table[21] = "sack";$table[22] = "safety";$table[23] = "tackle";$table[24] = "td";$table[25] = "team";// create csvfile namesfor ($i = 1; $i < 26; $i++) {    $csvfile[$i] = CSV_FILE_PATH . strtoupper($table[$i]) . '.csv';}// truncate tables -- only use this for 2000-2014 flushfor ($i = 1; $i < 26; $i++) {    mysqli_query($db, 'TRUNCATE TABLE ' . $table[$i]);}// load the data -- REPLACE INTO is used just in case the downloaded CSV files contain older records that require updating in your databasefor ($i = 1; $i < 26; $i++) {    mysqli_query($db, 'LOAD DATA LOCAL INFILE "' . $csvfile[$i] . '" REPLACE INTO TABLE ' . $table[$i] . ' FIELDS TERMINATED BY "," OPTIONALLY ENCLOSED BY """" IGNORE 1 LINES');}echo "Done";